export default function Bgm() {
    const bgm_dom=useRef(null)

    useEffect(()=>{
        bgm_dom.current=document.getElementById("bgm_player")
      bgm_dom.current.play();
    },[])

  return (
    <div><audio id="bgm_player" src='./bgm.mp3' autoPlay loop></audio></div>
  )
}
