import React,{useEffect,useRef,useState,useCallback} from 'react'
import "./index.scss"

export default function Navigator() {
  const arr=useRef(document.getElementsByClassName("navigator_items"));
  const [selected,setSelected]=useState(arr);

  const changeSelected=useCallback((e)=>{
    console.log(selected);
    setSelected(e.target);
  },[selected])

  return (
    <div id="navigator_container">
        <div className="navigator_items" onClick={changeSelected}>Programs</div>
        <div className="navigator_items" onClick={changeSelected}>Assignments</div>
    </div>
  )
}
