import React,{useEffect,useRef,useState} from 'react'

export default function Bgm() {
    const bgm_dom=useRef(null)
    const [interact_done,setInteract]=useState(false)
  
    useEffect(()=>{
      bgm_dom.current=document.getElementById("bgm_player")
      window.addEventListener("",()=>{setInteract(true)})
    },[])
  
    useEffect(()=>{
      if(interact_done)
      bgm_dom.current.play();
    },[interact_done])

  return (
    <div><audio id="bgm_player" src='./bgm.mp3' autoPlay loop></audio></div>
  )
}
